puts":[],"stateMutability":"payable","type":"function"},{"inputs":[{"internalType":"address","name":"","type":"address"}],"name":"stakers","outputs":[{"internalType":"uint256","name":"balance","type":"uint256"},{"internalType":"uint256","name":"stakingTime","type":"uint256"},{"internalType":"uint256","name":"lockDuration","type":"uint256"},{"internalType":"uint256","name":"unlockTime","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint256","name":"amount","type":"uint256"},{"internalType":"string","name":"evidenceURI","type":"string"},{"internalType":"uint256","name":"aiConfidence","type":"uint256"}],"name":"submitClaim","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[],"name":"totalAppealFees","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"totalPremiums","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"totalStaked","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"unstake","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"","type":"address"}],"name":"users","outputs":[{"internalType":"uint256","name":"totalPremiumPaid","type":"uint256"},{"internalType":"uint256","name":"coverage","type":"uint256"}],"stateMutability":"view","type":"function"},{"stateMutability":"payable","type":"receive"}],"devdoc":{"kind":"dev","methods":{},"version":1},"userdoc":{"kind":"user","methods":{},"version":1}},"settings":{"compilationTarget":{"contracts/Pool.sol":"Pool"},"evmVersion":"istanbul","libraries":{},"metadata":{"bytecodeHash":"ipfs","useLiteralContent":true},"optimizer":{"enabled":false,"runs":200},"remappings":[]},"sources":{"contracts/DAO.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./Pool.sol";

contract DAO {
    Pool public immutable pool;
    
    struct Proposal {
        int256 totalVotes;
        bool exists;
        uint256 aiConfidence;
        string evidenceURI;
        uint256 yesVotes;
        uint256 noVotes;
        uint256 timestamp;
        uint256 timelockEnd;
        bool isAppealProposal;
        mapping(address => bool) hasVoted;
    }

    uint256 public activeProposalCount;
    mapping(uint256 => Proposal) public proposals;
    
    uint256 public constant VOTING_DURATION = 3 days;
    uint256 public constant TIMELOCK_DURATION = 2 days;
    uint256 public constant QUORUM_PERCENTAGE = 51;
    uint256 public constant AI_APPROVAL_THRESHOLD = 60;
    uint256 public appealFee = 0.1 ether;

    event ProposalCreated(
        uint256 indexed claimId,
        string evidenceURI,
        uint256 aiConfidence,
        uint256 timestamp,
        bool isAppeal
    );
    
    event AppealSubmitted(
        uint256 indexed claimId,
        address indexed appellant,
        uint256 appealFee
    );

    event VoteSubmitted(
        uint256 indexed claimId,
        address indexed voter,
        bool vote,
        uint256 votingPower
    );
    
    event ProposalExecuted(
        uint256 indexed claimId,
        bool approved,
        uint256 finalYesVotes,
        uint256 finalNoVotes
    );

    constructor(address payable _poolAddress) {
        pool = Pool(_poolAddress);
    }

    function createProposal(
        uint256 claimId,
        string memory evidenceURI,
        uint256 aiConfidence
    ) external {
        require(msg.sender == address(pool), "Only pool can create proposals");
        require(!proposals[claimId].exists, "Proposal already exists");
        require(aiConfidence >= AI_APPROVAL_THRESHOLD, "AI confidence too low");

        activeProposalCount++;

        Proposal storage newProposal = proposals[claimId];
        newProposal.exists = true;
        newProposal.aiConfidence = aiConfidence;
        newProposal.evidenceURI = evidenceURI;
        newProposal.timestamp = block.timestamp;
        newProposal.timelockEnd = block.timestamp + TIMELOCK_DURATION;
        newProposal.isAppealProposal = false;

        emit ProposalCreated(
            claimId,
            evidenceURI,
            aiConfidence,
            block.timestamp,
            false
        );
    }

    function submitAppeal(
        uint256 claimId,
        string memory evidenceURI,
        uint256 aiConfidence
    ) external payable {
        require(!proposals[claimId].exists, "Proposal already exists");
        require(aiConfidence < AI_APPROVAL_THRESHOLD, "AI confidence too high");
        require(msg.value >= appealFee, "Insufficient appeal fee");

        (bool success, ) = address(pool).call{value: msg.value}("");
        require(success, "Fee transfer failed");

        activeProposalCount++;

        Proposal storage newProposal = proposals[claimId];
        newProposal.exists = true;
        newProposal.aiConfidence = aiConfidence;
        newProposal.evidenceURI = evidenceURI;
        newProposal.timestamp = block.timestamp;
        newProposal.timelockEnd = block.timestamp + TIMELOCK_DURATION;
        newProposal.isAppealProposal = true;

        emit AppealSubmitted(claimId, msg.sender, msg.value);
        emit ProposalCreated(
            claimId,
            evidenceURI,
            aiConfidence,
            block.timestamp,
            true
        );
    }

    function submitVote(uint256 claimId, bool vote) external {
        Proposal storage proposal = proposals[claimId];
        require(proposal.exists, "Proposal does not exist");
        require(!proposal.hasVoted[msg.sender], "Already voted");
        require(
            block.timestamp <= proposal.timelockEnd,
            "Timelock period ended"
        );

        uint256 votingPower = calculateVotingPower(msg.sender);
        require(votingPower > 0, "No voting power");

        if (vote) {
            proposal.yesVotes += votingPower;
            proposal.totalVotes += int256(votingPower);
        } else {
            proposal.noVotes += votingPower;
            proposal.totalVotes -= int256(votingPower);
        }

        proposal.hasVoted[msg.sender] = true;

        emit VoteSubmitted(claimId, msg.sender, vote, votingPower);
    }

    function executeProposal(uint256 claimId) public {
        Proposal storage proposal = proposals[claimId];
        require(proposal.exists, "Proposal does not exist");
        require(block.timestamp >= proposal.timelockEnd, "Timelock not ended");

        bool approved = proposal.totalVotes > 0 && 
            isQuorumReached(proposal.yesVotes + proposal.noVotes);

        if (approved) {
            pool.approveClaimByDAO(claimId);
        } else {
            pool.rejectClaimByDAO(claimId);
        }

        activeProposalCount--;
        
        emit ProposalExecuted(
            claimId,
            approved,
            proposal.yesVotes,
            proposal.noVotes
        );
    }

    function calculateVotingPower(address user) public view returns (uint256) {
        (uint256 premiumPaid, ) = pool.users(user);
        (uint256 stakedAmount, , , ) = pool.stakers(user);
        uint256 userContribution = premiumPaid + stakedAmount;
        
        uint256 totalPoolSize = pool.totalPremiums() + pool.totalStaked() + pool.totalAppealFees();
        if (totalPoolSize == 0) return 0;
        
        return (userContribution * 100) / totalPoolSize;
    }

    function isQuorumReached(uint256 totalVotes) internal view returns (bool) {
        uint256 totalPossibleVotes = pool.totalPremiums() + pool.totalStaked();
        return totalVotes * 100 >= totalPossibleVotes * QUORUM_PERCENTAGE;
    }

    function setAppealFee(uint256 newFee) external {
        require(msg.sender == address(pool), "Only pool can set fee");
        appealFee = newFee;
    }

    receive() external payable {}
}","keccak256":"0x3987b456400235b8b8b19b3ec0d2388d96cd382331769e1c37ad59bb0e2104b0","license":"MIT"},"contracts/Pool.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./DAO.sol";

contract Pool {
    DAO public dao;
    uint256 public constant TIMELOCK_DURATION = 2 days;
    uint256 public totalAppealFees;

    struct Plan {
        uint256 id;
        string name;
        uint256 premium;
        uint256 coverageAmount;
    }

    struct User {
        uint256 totalPremiumPaid;
        uint256 coverage;
    }

    struct Staker {
        uint256 balance;
        uint256 stakingTime;
        uint256 lockDuration;
        uint256 unlockTime;
    }

    struct Claim {
        address claimant;
        uint256 amount;
        uint256 unlockTime;
        ClaimStatus status;
        string evidenceURI;
        bool exists;
    }

    enum ClaimStatus { Timelock, DAO_Voting, Claimed, Rejected }

    mapping(address => User) public users;
    mapping(address => Staker) public stakers;
    mapping(uint256 => Claim) public claims;
    uint256 public claimCounter;
    uint256 public totalPremiums;
    uint256 public totalStaked;
    Plan[] public plans;

    event PremiumPaid(address indexed user, uint256 amount, uint256 coverage);
    event ClaimInitiated(uint256 claimId, address indexed claimant, uint256 amount, uint256 unlockTime, string evidenceURI);
    event ClaimApproved(uint256 claimId, address indexed claimant, uint256 amount);
    event ClaimRejected(uint256 claimId);
    event StakeDeposited(address indexed user, uint256 amount, uint256 lockDuration);
    event StakeWithdrawn(address indexed user, uint256 amount, bool withInterest);

    constructor() {
        _addPlan("Basic Shield", 0.01 ether, 5 ether);
        _addPlan("Premium Guard", 0.02 ether, 10 ether);
        _addPlan("Ultimate Protection", 0.03 ether, 20 ether);
    }

    function setDAO(address _daoAddress) external {
        require(address(dao) == address(0), "DAO already set");
        dao = DAO(payable(_daoAddress));
    }

    function _addPlan(string memory name, uint256 premium, uint256 coverageAmount) internal {
        plans.push(Plan({
            id: plans.length,
            name: name,
            premium: premium,
            coverageAmount: coverageAmount
        }));
    }

    function payPremium(uint256 planId) external payable {
        require(planId < plans.length, "Invalid plan ID");
        Plan memory plan = plans[planId];
        require(msg.value == plan.premium, "Incorrect premium amount");

        users[msg.sender].totalPremiumPaid += msg.value;
        users[msg.sender].coverage = plan.coverageAmount;
        totalPremiums += msg.value;

        emit PremiumPaid(msg.sender, msg.value, plan.coverageAmount);
    }

    function submitClaim(uint256 amount, string memory evidenceURI, uint256 aiConfidence) external {
        require(users[msg.sender].coverage >= amount, "Insufficient coverage");
        
        claimCounter++;
        claims[claimCounter] = Claim({
            claimant: msg.sender,
            amount: amount,
            unlockTime: block.timestamp + TIMELOCK_DURATION,
            status: ClaimStatus.Timelock,
            evidenceURI: evidenceURI,
            exists: true
        });

        if (aiConfidence >= dao.AI_APPROVAL_THRESHOLD()) {
            dao.createProposal(claimCounter, evidenceURI, aiConfidence);
        }

        emit ClaimInitiated(claimCounter, msg.sender, amount, block.timestamp + TIMELOCK_DURATION, evidenceURI);
    }

    function approveClaimByDAO(uint256 claimId) external {
        require(msg.sender == address(dao), "Only DAO can approve");
        Claim storage claim = claims[claimId];
        require(claim.exists, "Claim does not exist");
        require(claim.status == ClaimStatus.Timelock, "Invalid claim status");

        claim.status = ClaimStatus.Claimed;
        require(totalPremiums >= claim.amount, "Insufficient premiums");
        totalPremiums -= claim.amount;
        
        (bool success, ) = payable(claim.claimant).call{value: claim.amount}("");
        require(success, "Transfer failed");

        emit ClaimApproved(claimId, claim.claimant, claim.amount);
    }

    function rejectClaimByDAO(uint256 claimId) external {
        require(msg.sender == address(dao), "Only DAO can reject");
        Claim storage claim = claims[claimId];
        require(claim.exists, "Claim does not exist");
        claim.status = ClaimStatus.Rejected;
        emit ClaimRejected(claimId);
    }

    function stake(uint256 duration) external payable {
        require(msg.value > 0, "Must stake something");
        require(
            duration == 30 days || 
            duration == 180 days || 
            duration == 365 days, 
            "Invalid duration"
        );
        
        stakers[msg.sender].balance += msg.value;
        stakers[msg.sender].stakingTime = block.timestamp;
        stakers[msg.sender].lockDuration = duration;
        stakers[msg.sender].unlockTime = block.timestamp + duration;
        totalStaked += msg.value;

        emit StakeDeposited(msg.sender, msg.value, duration);
    }

    function unstake() external {
        Staker storage staker = stakers[msg.sender];
        require(staker.balance > 0, "No stake to withdraw");
        
        uint256 amount = staker.balance;
        uint256 stakingDuration = block.timestamp - staker.stakingTime;
        bool withInterest = stakingDuration >= staker.lockDuration;
        
        uint256 totalAmount = amount;
        if (withInterest) {
            totalAmount += _calculateInterest(amount, staker.lockDuration);
        }
        
        staker.balance = 0;
        staker.stakingTime = 0;
        staker.lockDuration = 0;
        staker.unlockTime = 0;
        totalStaked -= amount;

        (bool success, ) = payable(msg.sender).call{value: totalAmount}("");
        require(success, "Transfer failed");

        emit StakeWithdrawn(msg.sender, totalAmount, withInterest);
    }

    function _calculateInterest(uint256 amount, uint256 duration) internal pure returns (uint256) {
        if (duration == 30 days) return (amount * 3) / 1000;
        if (duration == 180 days) return (amount * 20) / 1000;
        if (duration == 365 days) return (amount * 50) / 1000;
        return 0;
    }

    receive() external payable {
        require(msg.sender == address(dao), "Only DAO can send funds");
        totalAppealFees += msg.value;
    }
}","keccak256":"0xba0c444fa812c5fae9789b1f00caae1ef1a2bbdbe17f6fbab7abad5c6dc0294f","license":"MIT"}},"version":1}
